import React from 'react'
import textareaAutoHeight from '../tools/textareaAutoHeight.js'


export default props => {

  React.useEffect(() => {
    textareaAutoHeight()
  }, [])

  return (
    <div className="NewPost">
      <form>
        <div id="NewPost-textdiv"><textarea className="NewPost-text" placeholder="Tell us what are you thinking..." /></div>
      </form>
    </div>
  )

}
