import React from 'react'
import Header from '../Header'
import Post from './Post'
import NewPost from './NewPost'
import './master.css'


export default props => {
  
  React.useEffect(() => {
    if (!localStorage.getItem('@react-web/auth')) {
      props.history.push('/login')
    }
  }, [])
  
  if (!localStorage.getItem('@react-web/auth')) return <div/>

  const [newPost, setNewPost] = React.useState({body: '', image: ''})

  function handleNewPost(e) {
    const {name, value} = e.target
  
    setNewPost({
      ...newPost,
      [name]: e.target.files[0] ? e.target.files[0] : value
    })

  }

  return (
    <div className="Home">
      <div className="Background bg_white"></div>
      <Header />
      <div className="PostViewer">
        <NewPost handleNewPost={handleNewPost} newPost={newPost} />
        <Post />
      </div>
    </div>
  )

}
