import React from 'react'
import $ from 'jquery'
import PostView from './PostView'


export default props => {

    const [posts, setPosts] = React.useState()

    const getPosts = () => {
        $.get(`${props.api}/getPosts`, r => {
            setPosts(r.map(post => (<PostView key={post.id} data={post} />)))
        })
    }

    React.useEffect(() => {
        getPosts()
        console.log(posts)
    }, [])

    // getPosts()

    return (
        <div className="Posts">
            {posts}
        </div>
    )

}
