import React from 'react'
import $ from 'jquery'
import View from './View'
import './master.css'


export default props => {

    const [posts, setPosts] = React.useState()

    function getPosts() {
        $.get(`${props.api}/getPosts`, r => {
            setPosts(r.map(post => (<View key={post.id} data={post} />)))
        })
    }

    function insertPost() {
        /*$.post(`${props.api}/insertPost`, {title: 'test'}, r => {
            console.log(r)
        })*/
        $.ajax({
            url: `${props.api}/insertPost`,
            method: 'POST',
            data: {title: 'algo'},
            success: r => {
                console.log(r)
            }
        })
    }

    React.useEffect(() => {
        getPosts()
    }, [])

    return (
        <div className="Posts">
            {posts}
            <button onClick={insertPost}>Postar</button>
        </div>
    )

}
