import React from 'react'
import textareaAutoHeight from '../tools/textareaAutoHeight'
import image from '../assets/img_addimage.png'
import like from '../assets/img_like.png'


export default props => {

  React.useEffect(() => {
    textareaAutoHeight('.NewPost-textdiv')
  }, [])

  return (
    <div className="NewPost">
      <form>
        <div id="NewPost-textdiv"><textarea className="NewPost-text" placeholder="Tell us what are you thinking..." /></div>
        <div><label className="NewPost-imageLbl" for="NewPost-addImage"><img src={image} className="img_actions"/></label><input type="file" id="NewPost-addImage"/><a>Publish</a></div>
      </form>
    </div>
  )

}
