import React from 'react'
import $ from 'jquery'
import PostView from './PostView'


export default props => {

    const [posts, setPosts] = React.useState()
    var x

    function getPosts() {
        $.get(`${props.api}/getPosts`, r => {
            //setPosts()
            x = r.map(post => (<PostView key={post.id} body={post.body} />))
        })
    }

    React.useEffect(() => {
        console.log(posts)
    })

    getPosts()

    return (
        <div className="Posts">
            {x}
        </div>
    )

}
